import React from 'react'
import { useParams } from 'react-router-dom'


function ProductDetails({ list }) {
    let id = useParams()
    
    return (
        <div className='productDetails'>
           {list.filter(item => item.id === id).map(filteredItem => (
                <li>
                {filteredItem.id}
                </li>
            ))} 
        </div>
    )
}

export default ProductDetails
