import React from 'react'
import { useParams } from 'react-router-dom'


function ProductDetails({ list }) {
    const people = [
  {
    name: 'James',
    age: 31,
  },
  {
    name: 'John',
    age: 45,
  },
  {
    name: 'Paul',
    age: 65,
  },
  {
    name: 'Ringo',
    age: 49,
  },
  {
    name: 'George',
    age: 34,
  }
]
    let id = useParams()
    
    return (
        <div className='productDetails'>
            {
                console.log(list.filter(item => (item.id === id)))
          }
        </div>
    )
}

export default ProductDetails
