export const initialState = {
    basket: ['bread'],
}

function reducer(state, action) {
    console.log(action)
    switch (action.type) {
        case 'ADD_TO_BASKET':
            //LOGIC FOR ADD PRODUCT TO BASKET
            return {state}
        case 'REMOVE_FROM_BASKET':
            //LOGIC FOR REMOVING ITEM FROM BASKET
            return {state}
        default:
            return state
        
    }
}

export default reducer
