import React from 'react'
import { useParams } from 'react-router-dom'
import ProductCarousel from './ProductCarousel'
import './ProductDetails.css'
import StarIcon from '@material-ui/icons/Star';
import ProductDetailsRight from './ProductDetailsRight';



function ProductDetails({list}) {
    
    let {id} = useParams()
    return (
        <div className='productDetails'>
            
              {list.filter(item => item.id == id).map(filterItem => (
                <>
                <div className='productDetails__left'>
                  <ProductCarousel key={filterItem.id}
                    image={filterItem.image}
                  />
                  </div>
                  <div className='productDetails__right'>
                    <ProductDetailsRight
                      id={filterItem.id}
                      title={filterItem.title}
                      image={filterItem.image}
                      price={filterItem.price}
                      rating
                    />
                    <div className='productDetails__info'>
                    <p>{filterItem.title}</p>
                    <div className='productDetails__price'>
                        <small>$</small>
                        <strong>{filterItem.price}</strong>
                    </div>
                    <div className='productDetails__rating'>
                        {
                            Array(filterItem.rating)
                            .fill()
                            .map((_) => <StarIcon />)  
                        }
                      </div>
                      <button className='productDetails__button'>Add To Basket</button>
                </div>
                </div>
                </>
              ))}
        </div>
    )
}

export default ProductDetails
